   109→                    <h4 class="text-subtitle-2 mb-2">Available Tags:</h4>
   110→                    <div v-if="category.tags && category.tags.length > 0" class="tags-list">
   111→                      <v-chip
   112→                        v-for="tag in category.tags"
   113→                        :key="tag.id || tag.name"
   114→                        :color="category.color"
   115→                        size="small"
   116→                        class="ma-1"
   117→                      >
   118→                        <v-icon start size="14">mdi-tag</v-icon>
   119→                        {{ tag.name }}
   120→                      </v-chip>
   121→                    </div>
   122→                    <p v-else class="text-body-2 text-medium-emphasis">No tags defined yet.</p>
   123→                  </div>
   124→
   125→                  <!-- Category Actions -->
   126→                  <div class="category-actions">
   127→                    <v-btn
   128→                      color="primary"
   129→                      variant="outlined"
   130→                      size="small"
   131→                      class="mr-2"
   132→                      @click="editCategory(category)"
   133→                    >
   134→                      <v-icon start>mdi-pencil</v-icon>
   135→                      Edit
   136→                    </v-btn>
   137→                    <v-btn
   138→                      color="error"
   139→                      variant="outlined"
   140→                      size="small"
   141→                      @click="deleteCategory(category)"
   142→                    >
   143→                      <v-icon start>mdi-delete</v-icon>
   144→                      Delete
   145→                    </v-btn>
   146→                  </div>
   147→                </div>
   148→              </v-expansion-panel-text>
   149→            </v-expansion-panel>
   150→          </v-expansion-panels>
   151→        </div>
   152→      </v-card-text>
   153→    </v-card>
   154→
   155→    <!-- Snackbar for notifications -->
   156→    <v-snackbar
   157→      v-model="snackbar.show"
   158→      :color="snackbar.color"
   159→      :timeout="4000"
   160→    >
   161→      {{ snackbar.message }}
   162→      <template #actions>
   163→        <v-btn variant="text" @click="snackbar.show = false">
   164→          Close
   165→        </v-btn>
   166→      </template>
   167→    </v-snackbar>
   168→  </div>
   169→</template>
   170→
   171→<script setup>
   172→import { ref, computed, onMounted } from 'vue';
   173→import { storeToRefs } from 'pinia';
   174→import { useOrganizerStore } from '../stores/organizer.js';
   175→
   176→// Store
   177→const organizerStore = useOrganizerStore();
   178→const { categories, loading } = storeToRefs(organizerStore);
   179→
   180→// Local state
   181→const creating = ref(false);
   182→const expandedPanels = ref([]);
   183→
   184→const newCategory = ref({
   185→  name: '',
   186→  color: '#1976d2'
   187→});
   188→
   189→const newCategoryErrors = ref({
   190→  name: [],
   191→  color: []
   192→});
   193→
   194→const snackbar = ref({
   195→  show: false,
   196→  message: '',
   197→  color: 'success'
   198→});
   199→
   200→// Computed
   201→const canCreateCategory = computed(() => {
   202→  return newCategory.value.name.trim().length > 0 && 
   203→         newCategory.value.color.match(/^#[0-9A-Fa-f]{6}$/);
   204→});
   205→
   206→// Methods
   207→const validateNewCategory = () => {
   208→  const errors = { name: [], color: [] };
   209→  
   210→  if (!newCategory.value.name.trim()) {
   211→    errors.name.push('Category name is required');
   212→  } else if (newCategory.value.name.trim().length > 50) {
   213→    errors.name.push('Category name must be 50 characters or less');
   214→  }
   215→  
   216→  // Check for duplicate names
   217→  if (categories.value.some(cat => 
   218→    cat.name.toLowerCase() === newCategory.value.name.trim().toLowerCase()
   219→  )) {
   220→    errors.name.push('Category name already exists');
   221→  }
   222→  
   223→  if (!newCategory.value.color.match(/^#[0-9A-Fa-f]{6}$/)) {
   224→    errors.color.push('Please select a valid color');
   225→  }
   226→  
   227→  newCategoryErrors.value = errors;
   228→  return errors.name.length === 0 && errors.color.length === 0;
   229→};
   230→
   231→const createCategory = async () => {
   232→  if (!validateNewCategory()) return;
   233→  
   234→  creating.value = true;
   235→  try {
   236→    await organizerStore.createCategory({
   237→      name: newCategory.value.name.trim(),
   238→      color: newCategory.value.color,
   239→      tags: [] // Start with empty tags, can be added later
   240→    });
   241→    
   242→    // Reset form
   243→    newCategory.value = {
   244→      name: '',
   245→      color: '#1976d2'
   246→    };
   247→    newCategoryErrors.value = { name: [], color: [] };
   248→    
   249→    showNotification(`Category "${newCategory.value.name}" created successfully`, 'success');
   250→  } catch (error) {
   251→    console.error('Failed to create category:', error);
   252→    showNotification('Failed to create category: ' + error.message, 'error');
   253→  } finally {
   254→    creating.value = false;
   255→  }
   256→};
   257→
   258→const editCategory = (category) => {
   259→  // TODO: Implement category editing dialog
   260→  showNotification('Category editing coming soon!', 'info');
   261→  console.log('Edit category:', category);
   262→};
   263→
   264→const deleteCategory = async (category) => {
   265→  if (!confirm(`Are you sure you want to delete the "${category.name}" category?`)) {
   266→    return;
   267→  }
   268→  
   269→  try {
   270→    await organizerStore.deleteCategory(category.id);
   271→    showNotification(`Category "${category.name}" deleted successfully`, 'success');
   272→  } catch (error) {
   273→    console.error('Failed to delete category:', error);
   274→    showNotification('Failed to delete category: ' + error.message, 'error');
   275→  }
   276→};
   277→
   278→const showNotification = (message, color = 'success') => {
   279→  snackbar.value = {
   280→    show: true,
   281→    message,
   282→    color
   283→  };
   284→};
   285→
   286→// Lifecycle
   287→onMounted(async () => {
   288→  // Ensure categories are loaded
   289→  if (!organizerStore.isInitialized || categories.value.length === 0) {
   290→    console.log('[CategoryManager] Initializing organizer store for categories...');
   291→    await organizerStore.initialize();
   292→  }
   293→  
   294→  // Expand first category by default
   295→  if (categories.value.length > 0) {
   296→    expandedPanels.value = [categories.value[0].id];
   297→  }
   298→});
   299→</script>
   300→
   301→<style scoped>
   302→.category-manager {
   303→  max-width: 1200px;
   304→  margin: 0 auto;
   305→  padding: 24px;
   306→}
   307→
   308→.manager-header {
   309→  text-align: center;
   310→}
   311→
   312→.category-details {
   313→  padding: 16px 0;
   314→}
   315→
   316→.tags-list {
   317→  min-height: 40px;
   318→}
   319→
   320→.category-actions {
   321→  border-top: 1px solid rgba(var(--v-border-color), var(--v-border-opacity));
   322→  padding-top: 16px;
   323→  margin-top: 16px;
   324→}
   325→</style>

