     1→<template>
     2→  <div class="view-mode-toggle">
     3→    <!-- Primary toggle: Flat vs Folder view -->
     4→    <v-btn-toggle
     5→      v-model="primaryViewMode"
     6→      mandatory
     7→      variant="outlined"
     8→      divided
     9→      class="primary-toggle"
    10→    >
    11→      <v-btn 
    12→        value="flat" 
    13→        size="small"
    14→        class="toggle-btn"
    15→      >
    16→        <v-icon size="16" class="mr-1">mdi-view-list</v-icon>
    17→        Flat View
    18→      </v-btn>
    19→      
    20→      <v-btn 
    21→        value="folders" 
    22→        size="small"
    23→        class="toggle-btn"
    24→      >
    25→        <v-icon size="16" class="mr-1">mdi-folder-multiple</v-icon>
    26→        Folder View
    27→      </v-btn>
    28→    </v-btn-toggle>
    29→    
    30→    <!-- Secondary toggle: List vs Grid layout within the view -->
    31→    <v-btn-toggle
    32→      v-model="secondaryDisplayMode"
    33→      mandatory
    34→      variant="outlined"
    35→      divided
    36→      class="secondary-toggle ml-3"
    37→    >
    38→      <v-btn 
    39→        value="list" 
    40→        size="small"
    41→        class="toggle-btn"
    42→        :disabled="loading"
    43→      >
    44→        <v-icon size="16">mdi-format-list-bulleted</v-icon>
    45→        <v-tooltip activator="parent" location="bottom">
    46→          List Layout
    47→        </v-tooltip>
    48→      </v-btn>
    49→      
    50→      <v-btn 
    51→        value="grid" 
    52→        size="small"
    53→        class="toggle-btn"
    54→        :disabled="loading"
    55→      >
    56→        <v-icon size="16">mdi-view-grid</v-icon>
    57→        <v-tooltip activator="parent" location="bottom">
    58→          Grid Layout
    59→        </v-tooltip>
    60→      </v-btn>
    61→    </v-btn-toggle>
    62→    
    63→    <!-- View mode status indicator -->
    64→    <div v-if="showStatusIndicator" class="view-status ml-3">
    65→      <v-chip
    66→        size="x-small"
    67→        variant="tonal"
    68→        :color="statusColor"
    69→        class="status-chip"
    70→      >
    71→        <v-icon size="12" class="mr-1">{{ statusIcon }}</v-icon>
    72→        {{ statusText }}
    73→      </v-chip>
    74→    </div>
    75→  </div>
    76→</template>
    77→
    78→<script setup>
    79→import { computed, ref, watch } from 'vue';
    80→import { useOrganizerStore } from '../stores/organizer.js';
    81→
    82→// Props
    83→const props = defineProps({
    84→  // Show status indicator chip
    85→  showStatusIndicator: {
    86→    type: Boolean,
    87→    default: true
    88→  },
    89→  
    90→  // Loading state
    91→  loading: {
    92→    type: Boolean,
    93→    default: false
    94→  },
    95→  
    96→  // Compact mode for smaller spaces
    97→  compact: {
    98→    type: Boolean,
    99→    default: false
   100→  }
   101→});
   102→
   103→// Emits
   104→const emit = defineEmits([
   105→  'view-mode-changed',
   106→  'display-mode-changed'
   107→]);
   108→
   109→// Store access
   110→const organizerStore = useOrganizerStore();
   111→
   112→// Local reactive state for toggles
   113→const primaryViewMode = ref('flat'); // 'flat' or 'folders'
   114→const secondaryDisplayMode = ref('list'); // 'list' or 'grid'
   115→
   116→// Computed properties
   117→const isFolderMode = computed(() => organizerStore.isFolderMode);
   118→const currentPath = computed(() => organizerStore.currentPath);
   119→const folderHierarchy = computed(() => organizerStore.folderHierarchy);
   120→
   121→// Status indicator computed properties
   122→const statusColor = computed(() => {
   123→  if (primaryViewMode.value === 'folders') {
   124→    return currentPath.value.length > 0 ? 'primary' : 'info';
   125→  }
   126→  return 'default';
   127→});
   128→
   129→const statusIcon = computed(() => {
   130→  if (primaryViewMode.value === 'folders') {
   131→    return currentPath.value.length > 0 ? 'mdi-folder-open' : 'mdi-folder-home';
   132→  }
   133→  return 'mdi-view-list';
   134→});
   135→
   136→const statusText = computed(() => {
   137→  if (primaryViewMode.value === 'folders') {
   138→    if (currentPath.value.length === 0) {
   139→      return 'All folders';
   140→    }
   141→    const depth = currentPath.value.length;
   142→    return `Level ${depth}`;
   143→  }
   144→  return 'Flat view';
   145→});
   146→
   147→// Initialize from store state
   148→const initializeFromStore = () => {
   149→  primaryViewMode.value = organizerStore.viewMode || 'flat';
   150→  // TODO: Initialize secondary display mode from store or localStorage
   151→  const savedDisplayMode = localStorage.getItem('organizer-display-mode');
   152→  secondaryDisplayMode.value = savedDisplayMode || 'list';
   153→};
   154→
   155→// Handle primary view mode changes
   156→watch(primaryViewMode, (newMode, oldMode) => {
   157→  if (newMode !== oldMode) {
   158→    organizerStore.setViewMode(newMode);
   159→    emit('view-mode-changed', {
   160→      mode: newMode,
   161→      previous: oldMode
   162→    });
   163→  }
   164→}, { immediate: false });
   165→
   166→// Handle secondary display mode changes
   167→watch(secondaryDisplayMode, (newMode, oldMode) => {
   168→  if (newMode !== oldMode) {
   169→    // Save to localStorage for persistence
   170→    localStorage.setItem('organizer-display-mode', newMode);
   171→    
   172→    emit('display-mode-changed', {
   173→      mode: newMode,
   174→      previous: oldMode
   175→    });
   176→  }
   177→}, { immediate: false });
   178→
   179→// Watch for store changes to sync local state
   180→watch(
   181→  () => organizerStore.viewMode,
   182→  (storeMode) => {
   183→    if (storeMode !== primaryViewMode.value) {
   184→      primaryViewMode.value = storeMode;
   185→    }
   186→  },
   187→  { immediate: true }
   188→);
   189→
   190→// Initialize component
   191→initializeFromStore();
   192→</script>
   193→
   194→<style scoped>
   195→.view-mode-toggle {
   196→  display: flex;
   197→  align-items: center;
   198→  flex-wrap: wrap;
   199→  gap: 12px;
   200→}
   201→
   202→.primary-toggle,
