watch(
  () => organizerStore.viewMode,
  (storeMode) => {
    if (storeMode !== primaryViewMode.value) {
      primaryViewMode.value = storeMode;
    }
  },
  { immediate: true }
);

// Initialize component
initializeFromStore();

