     6â†’        v-for="tag in tags"
     7â†’        :key="tag"
     8â†’        size="small"
     9â†’        closable
    10â†’        variant="outlined"
    11â†’        color="primary"
    12â†’        class="ma-1"
    13â†’        @click:close="removeTag(tag)"
    14â†’      >
    15â†’        {{ tag }}
    16â†’      </v-chip>
    17â†’    </div>
    18â†’
    19â†’    <!-- Input for adding new tags -->
    20â†’    <div class="tag-input-field">
    21â†’      <v-text-field
    22â†’        v-model="newTagText"
    23â†’        :placeholder="placeholder"
    24â†’        :disabled="disabled || loading"
    25â†’        :loading="loading"
    26â†’        variant="outlined"
    27â†’        density="compact"
    28â†’        hide-details="auto"
    29â†’        clearable
    30â†’        @keydown.enter="handleEnterKey"
    31â†’        @keydown="handleKeydown"
    32â†’        @keydown.tab="handleTabKey"
    33â†’      >
    34â†’        <template #append-inner>
    35â†’          <v-btn
    36â†’            v-if="newTagText.trim()"
    37â†’            icon
    38â†’            size="small"
    39â†’            variant="text"
    40â†’            :disabled="disabled || loading"
    41â†’            @click="addTag"
    42â†’          >
    43â†’            <v-icon size="16">mdi-plus</v-icon>
    44â†’          </v-btn>
    45â†’        </template>
    46â†’      </v-text-field>
    47â†’    </div>
    48â†’
    49â†’    <!-- Helper text -->
    50â†’    <div v-if="showHelperText && !hideDetails" class="helper-text mt-1">
    51â†’      <small class="text-grey">
    52â†’        {{ helperText }}
    53â†’      </small>
    54â†’    </div>
    55â†’
    56â†’    <!-- Error message -->
    57â†’    <div v-if="errorMessage" class="error-message mt-1">
    58â†’      <small class="text-error">
    59â†’        {{ errorMessage }}
    60â†’      </small>
    61â†’    </div>
    62â†’  </div>
    63â†’</template>
    64â†’
    65â†’<script setup>
    66â†’import { ref, computed, watch } from 'vue';
    67â†’
    68â†’// Props
    69â†’const props = defineProps({
    70â†’  modelValue: {
    71â†’    type: Array,
    72â†’    default: () => [],
    73â†’  },
    74â†’  placeholder: {
    75â†’    type: String,
    76â†’    default: 'Add tags...',
    77â†’  },
    78â†’  disabled: {
    79â†’    type: Boolean,
    80â†’    default: false,
    81â†’  },
    82â†’  loading: {
    83â†’    type: Boolean,
    84â†’    default: false,
    85â†’  },
    86â†’  maxTags: {
    87â†’    type: Number,
    88â†’    default: 20,
    89â†’  },
    90â†’  hideDetails: {
    91â†’    type: Boolean,
    92â†’    default: false,
    93â†’  },
    94â†’  allowDuplicates: {
    95â†’    type: Boolean,
    96â†’    default: false,
    97â†’  },
    98â†’  validateTag: {
    99â†’    type: Function,
   100â†’    default: null,
   101â†’  },
   102â†’});
   103â†’
   104â†’// Emits
   105â†’const emit = defineEmits([
   106â†’  'update:modelValue',
   107â†’  'tag-added',
   108â†’  'tag-removed',
   109â†’  'error',
   110â†’]);
   111â†’
   112â†’// State
   113â†’const newTagText = ref('');
   114â†’const errorMessage = ref('');
   115â†’
   116â†’// Computed
   117â†’const tags = computed(() => props.modelValue || []);
   118â†’
   119â†’const showHelperText = computed(() => {
   120â†’  return !errorMessage.value && tags.value.length === 0;
   121â†’});
   122â†’
   123â†’const helperText = computed(() => {
   124â†’  return 'Press Enter, Tab, or comma to add tags';
   125â†’});
   126â†’
   127â†’// Watch for external changes to clear errors
   128â†’watch(() => props.modelValue, () => {
   129â†’  errorMessage.value = '';
   130â†’});
   131â†’
   132â†’// Methods
   133â†’const validateNewTag = (tagText) => {
   134â†’  const tag = tagText.trim();
   135â†’
   136â†’  // Empty tag
   137â†’  if (!tag) {
   138â†’    return { valid: false, error: 'Tag cannot be empty' };
   139â†’  }
   140â†’
   141â†’  // Tag too long
   142â†’  if (tag.length > 50) {
   143â†’    return { valid: false, error: 'Tag cannot be longer than 50 characters' };
   144â†’  }
   145â†’
   146â†’  // Too many tags
   147â†’  if (tags.value.length >= props.maxTags) {
   148â†’    return { valid: false, error: `Cannot add more than ${props.maxTags} tags` };
   149â†’  }
   150â†’
   151â†’  // Duplicate tag (if not allowed)
   152â†’  if (!props.allowDuplicates && tags.value.includes(tag)) {
   153â†’    return { valid: false, error: 'Tag already exists' };
   154â†’  }
   155â†’
   156â†’  // Custom validation
   157â†’  if (props.validateTag) {
   158â†’    const customValidation = props.validateTag(tag);
   159â†’    if (customValidation !== true) {
   160â†’      return { 
   161â†’        valid: false, 
   162â†’        error: typeof customValidation === 'string' ? customValidation : 'Invalid tag'
   163â†’      };
   164â†’    }
   165â†’  }
   166â†’
   167â†’  return { valid: true };
   168â†’};
   169â†’
   170â†’const addTag = () => {
   171â†’  if (props.disabled || props.loading) return;
   172â†’
   173â†’  const tagText = newTagText.value.trim();
   174â†’  if (!tagText) return;
   175â†’
   176â†’  // Validate tag
   177â†’  const validation = validateNewTag(tagText);
   178â†’  if (!validation.valid) {
   179â†’    errorMessage.value = validation.error;
   180â†’    emit('error', validation.error);
   181â†’    return;
   182â†’  }
   183â†’
   184â†’  // Add tag
   185â†’  const newTags = [...tags.value, tagText];
   186â†’  emit('update:modelValue', newTags);
   187â†’  emit('tag-added', tagText);
   188â†’
   189â†’  // Clear input and error
   190â†’  newTagText.value = '';
   191â†’  errorMessage.value = '';
   192â†’};
   193â†’
   194â†’const removeTag = (tagToRemove) => {
   195â†’  if (props.disabled || props.loading) return;
   196â†’
   197â†’  const newTags = tags.value.filter(tag => tag !== tagToRemove);
   198â†’  emit('update:modelValue', newTags);
   199â†’  emit('tag-removed', tagToRemove);
   200â†’
   201â†’  // Clear error when removing tags
   202â†’  errorMessage.value = '';
   203â†’};
   204â†’
   205â†’// Keyboard event handlers
   206â†’const handleEnterKey = (event) => {
   207â†’  event.preventDefault();
   208â†’  addTag();
   209â†’};
   210â†’
   211â†’const handleKeydown = (event) => {
   212â†’  if (event.key === ',') {
   213â†’    event.preventDefault();
   214â†’    addTag();
   215â†’  }
   216â†’};
   217â†’
   218â†’const handleTabKey = (event) => {
   219â†’  if (newTagText.value.trim()) {
   220â†’    event.preventDefault();
   221â†’    addTag();
   222â†’  }
   223â†’};
   224â†’
   225â†’// Expose methods for external access
   226â†’defineExpose({
   227â†’  addTag,
   228â†’  removeTag,
   229â†’  clearInput: () => {
   230â†’    newTagText.value = '';
   231â†’    errorMessage.value = '';
   232â†’  },
   233â†’  focus: () => {
   234â†’    // Focus would be handled by accessing the text field ref
   235â†’  }
   236â†’});
   237â†’</script>
   238â†’
