   241â†’  width: 100%;
   242â†’}
   243â†’
   244â†’.tags-display {
   245â†’  min-height: 0;
   246â†’}
   247â†’
   248â†’.tag-input-field {
   249â†’  width: 100%;
   250â†’}
   251â†’
   252â†’.helper-text {
   253â†’  font-size: 12px;
   254â†’  opacity: 0.7;
   255â†’}
   256â†’
   257â†’.error-message {
   258â†’  font-size: 12px;
   259â†’}
   260â†’
   261â†’/* Ensure chips wrap nicely */
   262â†’.tags-display :deep(.v-chip) {
   263â†’  margin: 2px;
   264â†’}
   265â†’
   266â†’/* Custom text field styling */
   267â†’.tag-input-field :deep(.v-field) {
