  const resetUploadStatus = () => {
    uploadStatus.value = {
      successful: 0,
      failed: 0,
      previouslyUploaded: 0,
      isUploading: false,
      isPaused: false,
      pauseRequested: false,
      currentFile: null,
      currentAction: null,
      currentUploadIndex: 0
    }
  }

