      console.error('[Organizer] Failed to add tag:', err);
      throw err;
    }
  };

  /**
   * Remove a single tag from evidence document
   */
  const removeTag = async (evidenceId, tagText) => {
    try {
      const evidence = evidenceList.value.find(e => e.id === evidenceId);
