   * Set filter text and apply filters
   */
  const setFilter = (text) => {
    filterText.value = text;
    applyFilters();
  };

  /**
   * Clear all filters
   */
  const clearFilters = () => {
    filterText.value = '';
    applyFilters();
  };

  /**
   * Reset store to initial state
   */
  const reset = () => {
    evidenceList.value = [];
    filteredEvidence.value = [];
    loading.value = false;
    error.value = null;
    filterText.value = '';
    isInitialized.value = false;
  };

  return {
    // State
    evidenceList,
    filteredEvidence,
    loading,
    error,
    filterText,
    isInitialized,

    // Computed
    evidenceCount,
    filteredCount,

    // Actions
    loadEvidence,
    updateEvidenceTags,
    addTag,
    removeTag,
    setFilter,
    clearFilters,
    reset,
  };
});

