   208â†’class UploadSpeedCalculator {
   209â†’  constructor(windowSize = 10) {
   210â†’    this.windowSize = windowSize;
   211â†’    this.dataPoints = [];
   212â†’    this.totalBytes = 0;
   213â†’    this.startTime = Date.now();
