   215â†’
   216â†’  addDataPoint(bytesTransferred, timestamp = Date.now()) {
   217â†’    this.dataPoints.push({ bytes: bytesTransferred, time: timestamp });
   218â†’
   219â†’    // Keep only the most recent data points
   220â†’    if (this.dataPoints.length > this.windowSize) {
   221â†’      this.dataPoints.shift();
   222â†’    }
   223â†’
   224â†’    this.totalBytes = Math.max(this.totalBytes, bytesTransferred);
   225â†’  }
   226â†’
   227â†’  getCurrentSpeed() {
   228â†’    if (this.dataPoints.length < 2) return 0;
   229â†’
   230â†’    const recent = this.dataPoints.slice(-5); // Use last 5 data points
   231â†’    if (recent.length < 2) return 0;
   232â†’
   233â†’    const timeDiff = recent[recent.length - 1].time - recent[0].time;
   234â†’    const bytesDiff = recent[recent.length - 1].bytes - recent[0].bytes;
   235â†’
   236â†’    return timeDiff > 0 ? (bytesDiff / timeDiff) * 1000 : 0; // bytes per second
   237â†’  }
   238â†’
   239â†’  getAverageSpeed() {
   240â†’    if (this.dataPoints.length === 0) return 0;
   241â†’
