     1‚Üí<template>
     2‚Üí  <div class="organizer-container">
     3‚Üí    <!-- Header with title and search -->
     4‚Üí    <OrganizerHeader
     5‚Üí      v-model:search-text="searchText"
     6‚Üí      :evidence-count="evidenceCount"
     7‚Üí      :filtered-count="filteredCount"
     8‚Üí      @search="handleSearch"
     9‚Üí      @manage-categories="navigateToCategories"
    10‚Üí    />
    11‚Üí
    12‚Üí    <!-- States (loading, error, empty) -->
    13‚Üí    <OrganizerStates
    14‚Üí      :loading="loading"
    15‚Üí      :is-initialized="isInitialized"
    16‚Üí      :error="error"
    17‚Üí      :evidence-count="evidenceCount"
    18‚Üí      :filtered-count="filteredCount"
    19‚Üí      @retry="retryLoad"
    20‚Üí      @clear-search="clearSearch"
    21‚Üí    />
    22‚Üí
    23‚Üí    <!-- File list display -->
    24‚Üí    <FileListDisplay
    25‚Üí      v-if="showFileList"
    26‚Üí      v-model:view-mode="viewMode"
    27‚Üí      :filtered-evidence="filteredEvidence"
    28‚Üí      :getEvidenceTags="getEvidenceTags"
    29‚Üí      :getTagUpdateLoading="getTagUpdateLoading"
    30‚Üí      :getAIProcessing="getAIProcessing"
    31‚Üí      @tags-update="handleTagsUpdate"
    32‚Üí      @download="downloadFile"
    33‚Üí      @rename="renameFile"
    34‚Üí      @view-details="viewDetails"
    35‚Üí      @process-with-ai="processWithAI"
    36‚Üí    />
    37‚Üí
    38‚Üí    <!-- Loading overlay for updates -->
    39‚Üí    <v-overlay
    40‚Üí      v-model="showUpdateOverlay"
    41‚Üí      class="d-flex align-center justify-center"
    42‚Üí      contained
    43‚Üí    >
    44‚Üí      <v-progress-circular indeterminate size="48" />
    45‚Üí      <p class="ml-4">Updating tags...</p>
    46‚Üí    </v-overlay>
    47‚Üí
    48‚Üí    <!-- AI Tag Review Modal -->
    49‚Üí    <AITagReview
    50‚Üí      v-model="showAIReview"
    51‚Üí      :evidence="currentReviewEvidence"
    52‚Üí      :loading="aiReviewLoading"
    53‚Üí      :error="aiReviewError"
    54‚Üí      @approve-tag="handleApproveTag"
    55‚Üí      @reject-tag="handleRejectTag"
    56‚Üí      @approve-all="handleApproveAll"
    57‚Üí      @reject-all="handleRejectAll"
    58‚Üí      @save-changes="handleSaveAIReview"
    59‚Üí      @retry-load="retryAIReview"
    60‚Üí      @close="closeAIReview"
    61‚Üí    />
    62‚Üí
    63‚Üí    <!-- Snackbar for notifications -->
    64‚Üí    <v-snackbar
    65‚Üí      v-model="snackbar.show"
    66‚Üí      :color="snackbar.color"
    67‚Üí      :timeout="snackbar.timeout"
    68‚Üí    >
    69‚Üí      {{ snackbar.message }}
    70‚Üí      <template #actions>
    71‚Üí        <v-btn variant="text" @click="snackbar.show = false">
    72‚Üí          Close
    73‚Üí        </v-btn>
    74‚Üí      </template>
    75‚Üí    </v-snackbar>
    76‚Üí  </div>
    77‚Üí</template>
    78‚Üí
    79‚Üí<script setup>
    80‚Üíimport { ref, computed, onMounted, onBeforeUnmount } from 'vue';
    81‚Üíimport { storeToRefs } from 'pinia';
    82‚Üíimport { useRouter } from 'vue-router';
    83‚Üíimport { useOrganizerStore } from '../stores/organizer.js';
    84‚Üíimport { AITagService } from '../services/aiTagService.js';
    85‚Üíimport OrganizerHeader from '../components/OrganizerHeader.vue';
    86‚Üíimport OrganizerStates from '../components/OrganizerStates.vue';
    87‚Üíimport FileListDisplay from '../components/FileListDisplay.vue';
    88‚Üíimport AITagReview from '../components/AITagReview.vue';
    89‚Üí
    90‚Üí// Store and router
    91‚Üíconst organizerStore = useOrganizerStore();
    92‚Üíconst router = useRouter();
    93‚Üí
    94‚Üí// State
    95‚Üíconst searchText = ref('');
    96‚Üíconst viewMode = ref('list');
    97‚Üíconst showUpdateOverlay = ref(false);
    98‚Üíconst tagUpdateLoading = ref(new Set());
    99‚Üíconst aiProcessing = ref(new Set()); // Track AI processing by evidence ID
   100‚Üíconst unsubscribe = ref(null);
   101‚Üí
   102‚Üí// AI Review Modal State
   103‚Üíconst showAIReview = ref(false);
   104‚Üíconst currentReviewEvidence = ref(null);
   105‚Üíconst aiReviewLoading = ref(false);
   106‚Üíconst aiReviewError = ref(null);
   107‚Üí
   108‚Üí// AI Service instance
   109‚Üíconst aiTagService = new AITagService();
   110‚Üí
   111‚Üíconst snackbar = ref({
   112‚Üí  show: false,
   113‚Üí  message: '',
   114‚Üí  color: 'success',
   115‚Üí  timeout: 4000
   116‚Üí});
   117‚Üí
   118‚Üí// Computed - use storeToRefs for reactive properties
   119‚Üíconst {
   120‚Üí  filteredEvidence,
   121‚Üí  loading,
   122‚Üí  error,
   123‚Üí  evidenceCount,
   124‚Üí  filteredCount,
   125‚Üí  isInitialized
   126‚Üí} = storeToRefs(organizerStore);
   127‚Üí
   128‚Üí// Computed for conditional display
   129‚Üíconst showFileList = computed(() => {
   130‚Üí  return !loading.value && !error.value && evidenceCount.value > 0 && filteredCount.value > 0 && isInitialized.value;
   131‚Üí});
   132‚Üí
   133‚Üí// Methods
   134‚Üíconst handleSearch = (value) => {
   135‚Üí  organizerStore.setFilter(value || '');
   136‚Üí};
   137‚Üí
   138‚Üíconst clearSearch = () => {
   139‚Üí  searchText.value = '';
   140‚Üí  organizerStore.clearFilters();
   141‚Üí};
   142‚Üí
   143‚Üíconst retryLoad = async () => {
   144‚Üí  await organizerStore.loadEvidence();
   145‚Üí};
   146‚Üí
   147‚Üíconst navigateToCategories = () => {
   148‚Üí  router.push('/organizer/categories');
   149‚Üí};
   150‚Üí
   151‚Üí// Helper methods for child components
   152‚Üíconst getEvidenceTags = (evidence) => {
   153‚Üí  return organizerStore.getAllTags(evidence);
   154‚Üí};
   155‚Üí
   156‚Üíconst getTagUpdateLoading = (evidenceId) => {
   157‚Üí  return tagUpdateLoading.value.has(evidenceId);
   158‚Üí};
   159‚Üí
   160‚Üíconst getAIProcessing = (evidenceId) => {
   161‚Üí  return aiProcessing.value.has(evidenceId);
   162‚Üí};
   163‚Üí
   164‚Üíconst handleTagsUpdate = async (evidenceId, newTags) => {
   165‚Üí  try {
   166‚Üí    tagUpdateLoading.value.add(evidenceId);
   167‚Üí    await organizerStore.updateEvidenceTags(evidenceId, newTags);
   168‚Üí    
   169‚Üí    showNotification('Tags updated successfully', 'success');
   170‚Üí  } catch (error) {
   171‚Üí    console.error('Failed to update tags:', error);
   172‚Üí    showNotification('Failed to update tags', 'error');
   173‚Üí  } finally {
   174‚Üí    tagUpdateLoading.value.delete(evidenceId);
   175‚Üí  }
   176‚Üí};
   177‚Üí
   178‚Üíconst downloadFile = () => {
   179‚Üí  showNotification('Download functionality coming soon', 'info');
   180‚Üí};
   181‚Üí
   182‚Üíconst renameFile = () => {
   183‚Üí  showNotification('Rename functionality coming soon', 'info');
   184‚Üí};
   185‚Üí
   186‚Üíconst viewDetails = () => {
   187‚Üí  showNotification('Details view coming soon', 'info');
   188‚Üí};
   189‚Üí
   190‚Üíconst processWithAI = async (evidence) => {
   191‚Üí  console.log('DEBUG: processWithAI called with evidence:', evidence.id);
   192‚Üí  console.log('DEBUG: aiTagService.isAIEnabled():', aiTagService.isAIEnabled());
   193‚Üí  
   194‚Üí  if (!aiTagService.isAIEnabled()) {
   195‚Üí    console.log('DEBUG: AI features not enabled');
   196‚Üí    showNotification('AI features are not enabled', 'warning');
   197‚Üí    return;
   198‚Üí  }
   199‚Üí
   200‚Üí  try {
   201‚Üí    aiProcessing.value.add(evidence.id);
   202‚Üí    showNotification('Processing document with AI...', 'info', 2000);
   203‚Üí
   204‚Üí    const result = await aiTagService.processSingleDocument(evidence.id);
   205‚Üí    
   206‚Üí    if (result.success) {
   207‚Üí      if (result.suggestedTags.length > 0) {
   208‚Üí        showNotification(
   209‚Üí          `AI processing complete! ${result.suggestedTags.length} tags applied.`, 
   210‚Üí          'success'
   211‚Üí        );
   212‚Üí      } else {
   213‚Üí        showNotification('AI processing complete, but no tags were suggested.', 'info');
   214‚Üí      }
   215‚Üí    } else {
   216‚Üí      throw new Error(result.error || 'AI processing failed');
   217‚Üí    }
   218‚Üí  } catch (error) {
   219‚Üí    console.error('AI processing failed:', error);
   220‚Üí    
   221‚Üí    // User-friendly error messages
   222‚Üí    let errorMessage = 'AI processing failed';
   223‚Üí    if (error.message.includes('File size')) {
   224‚Üí      errorMessage = 'File too large for AI processing (max 20MB)';
   225‚Üí    } else if (error.message.includes('categories')) {
   226‚Üí      errorMessage = 'Please create categories before using AI tagging';
   227‚Üí    } else if (error.message.includes('not found')) {
   228‚Üí      errorMessage = 'Document not found';
   229‚Üí    }
   230‚Üí    
   231‚Üí    showNotification(errorMessage, 'error');
   232‚Üí  } finally {
   233‚Üí    aiProcessing.value.delete(evidence.id);
   234‚Üí  }
   235‚Üí};
   236‚Üí
   237‚Üíconst showNotification = (message, color = 'success', timeout = 4000) => {
   238‚Üí  snackbar.value = {
   239‚Üí    show: true,
   240‚Üí    message,
   241‚Üí    color,
   242‚Üí    timeout
   243‚Üí  };
   244‚Üí};
   245‚Üí
   246‚Üí// AI Review Modal Handlers
   247‚Üíconst handleApproveTag = (tag) => {
   248‚Üí  console.log('Tag approved:', tag);
   249‚Üí  // Individual tag approval is handled by the modal component
   250‚Üí  // The save operation will handle the actual database updates
   251‚Üí};
   252‚Üí
   253‚Üíconst handleRejectTag = (tag) => {
   254‚Üí  console.log('Tag rejected:', tag);
   255‚Üí  // Individual tag rejection is handled by the modal component
   256‚Üí  // The save operation will handle the actual database updates
   257‚Üí};
   258‚Üí
   259‚Üíconst handleApproveAll = (tags) => {
   260‚Üí  console.log('All tags approved:', tags);
   261‚Üí  // Batch approval handled by modal component
   262‚Üí};
   263‚Üí
   264‚Üíconst handleRejectAll = (tags) => {
   265‚Üí  console.log('All tags rejected:', tags);
   266‚Üí  // Batch rejection handled by modal component
   267‚Üí};
   268‚Üí
   269‚Üíconst handleSaveAIReview = async (changes) => {
   270‚Üí  if (!currentReviewEvidence.value) return;
   271‚Üí
   272‚Üí  try {
   273‚Üí    aiReviewLoading.value = true;
   274‚Üí    
   275‚Üí    // Process the review changes using AITagService
   276‚Üí    const result = await aiTagService.processReviewChanges(
   277‚Üí      currentReviewEvidence.value.id,
   278‚Üí      changes
   279‚Üí    );
   280‚Üí
   281‚Üí    if (result.success) {
   282‚Üí      const { approvedCount, rejectedCount, errors } = result.results;
   283‚Üí      
   284‚Üí      if (errors.length > 0) {
   285‚Üí        showNotification(
   286‚Üí          `Partial success: ${approvedCount} approved, ${rejectedCount} rejected. ${errors.length} failed.`,
   287‚Üí          'warning'
   288‚Üí        );
   289‚Üí      } else {
   290‚Üí        showNotification(
   291‚Üí          `Successfully applied changes: ${approvedCount} approved, ${rejectedCount} rejected.`,
   292‚Üí          'success'
   293‚Üí        );
   294‚Üí      }
   295‚Üí    } else {
   296‚Üí      throw new Error('Review processing failed');
   297‚Üí    }
   298‚Üí    
   299‚Üí    closeAIReview();
   300‚Üí  } catch (error) {
   301‚Üí    console.error('Failed to save AI review changes:', error);
   302‚Üí    showNotification('Failed to save changes. Please try again.', 'error');
   303‚Üí  } finally {
   304‚Üí    aiReviewLoading.value = false;
   305‚Üí  }
   306‚Üí};
   307‚Üí
   308‚Üíconst retryAIReview = () => {
   309‚Üí  // Retry loading AI suggestions (if needed)
   310‚Üí  aiReviewError.value = null;
   311‚Üí};
   312‚Üí
   313‚Üíconst closeAIReview = () => {
   314‚Üí  showAIReview.value = false;
   315‚Üí  currentReviewEvidence.value = null;
   316‚Üí  aiReviewError.value = null;
   317‚Üí};
   318‚Üí
   319‚Üí// Lifecycle
   320‚ÜíonMounted(async () => {
   321‚Üí  try {
   322‚Üí    // Initialize all stores (evidence + categories)
   323‚Üí    const { evidenceUnsubscribe } = await organizerStore.initialize();
   324‚Üí    unsubscribe.value = evidenceUnsubscribe;
   325‚Üí  } catch (error) {
   326‚Üí    console.error('Failed to initialize organizer:', error);
   327‚Üí    showNotification('Failed to load documents', 'error');
   328‚Üí  }
   329‚Üí});
   330‚Üí
   331‚ÜíonBeforeUnmount(() => {
   332‚Üí  // Clean up listeners
   333‚Üí  if (unsubscribe.value) {
   334‚Üí    unsubscribe.value();
   335‚Üí  }
   336‚Üí  organizerStore.reset();
   337‚Üí});
   338‚Üí</script>
   339‚Üí
   340‚Üí<style scoped>
   341‚Üí.organizer-container {
   342‚Üí  height: 100vh;
   343‚Üí  display: flex;
   344‚Üí  flex-direction: column;
   345‚Üí  overflow: hidden;
   346‚Üí}
   347‚Üí
   348‚Üí/* Simplified styles - component-specific styles moved to respective components */
   349‚Üí</style>

