     1‚Üíimport { defineStore } from 'pinia';
     2‚Üíimport { computed, watch } from 'vue';
     3‚Üíimport { doc, updateDoc, serverTimestamp } from 'firebase/firestore';
     4‚Üíimport { db } from '../../../services/firebase.js';
     5‚Üíimport { useAuthStore } from '../../../core/stores/auth.js';
     6‚Üí
     7‚Üí// Import decomposed stores
     8‚Üíimport { useOrganizerCoreStore } from './organizerCore.js';
     9‚Üíimport { useOrganizerQueryStore } from './organizerQueryStore.js';
    10‚Üíimport { useCategoryStore } from './categoryStore.js';
    11‚Üíimport { useTagStore } from './tagStore.js';
    12‚Üíimport { useMigrationStore } from './migrationStore.js';
    13‚Üí
    14‚Üí/**
    15‚Üí * Main Organizer Store - Facade pattern combining decomposed stores
    16‚Üí * Maintains backward compatibility while providing structured functionality
    17‚Üí */
    18‚Üíexport const useOrganizerStore = defineStore('organizer', () => {
    19‚Üí  // Get store instances
    20‚Üí  const coreStore = useOrganizerCoreStore();
    21‚Üí  const queryStore = useOrganizerQueryStore();
    22‚Üí  const categoryStore = useCategoryStore();
    23‚Üí  const tagStore = useTagStore();
    24‚Üí  const migrationStore = useMigrationStore();
    25‚Üí  const authStore = useAuthStore();
    26‚Üí
    27‚Üí  // Watch core store evidence changes to update query store
    28‚Üí  watch(
    29‚Üí    () => coreStore.evidenceList,
    30‚Üí    () => {
    31‚Üí      queryStore.initializeFilters();
    32‚Üí    },
    33‚Üí    { deep: true }
    34‚Üí  );
    35‚Üí
    36‚Üí  // Computed properties combining data from multiple stores
    37‚Üí  const evidenceCount = computed(() => coreStore.evidenceCount);
    38‚Üí  const filteredCount = computed(() => queryStore.filteredCount);
    39‚Üí  const isInitialized = computed(() => 
    40‚Üí    coreStore.isInitialized && categoryStore.isInitialized
    41‚Üí  );
    42‚Üí
    43‚Üí  /**
    44‚Üí   * Initialize all stores
    45‚Üí   */
    46‚Üí  const initialize = async () => {
    47‚Üí    try {
    48‚Üí      // Load evidence and categories in parallel
    49‚Üí      const [evidenceUnsubscribe, categoryUnsubscribe] = await Promise.all([
    50‚Üí        coreStore.loadEvidence(),
    51‚Üí        categoryStore.loadCategories()
    52‚Üí      ]);
    53‚Üí      
    54‚Üí      return { evidenceUnsubscribe, categoryUnsubscribe };
    55‚Üí    } catch (err) {
    56‚Üí      console.error('[OrganizerStore] Failed to initialize:', err);
    57‚Üí      throw err;
    58‚Üí    }
    59‚Üí  };
    60‚Üí
    61‚Üí  /**
    62‚Üí   * Legacy method: Update evidence tags (backward compatibility)
    63‚Üí   * Now handles both legacy and structured tags
    64‚Üí   */
    65‚Üí  const updateEvidenceTags = async (evidenceId, newTags) => {
    66‚Üí    try {
    67‚Üí      if (!authStore.isAuthenticated) {
    68‚Üí        throw new Error('User not authenticated');
    69‚Üí      }
    70‚Üí
    71‚Üí      const teamId = authStore.currentTeam;
    72‚Üí      if (!teamId) {
    73‚Üí        throw new Error('No team ID available');
    74‚Üí      }
    75‚Üí
    76‚Üí      // Validate tags
    77‚Üí      if (!Array.isArray(newTags)) {
    78‚Üí        throw new Error('Tags must be an array');
    79‚Üí      }
    80‚Üí
    81‚Üí      // Clean and deduplicate tags
    82‚Üí      const cleanedTags = [...new Set(
    83‚Üí        newTags
    84‚Üí          .map(tag => tag.toString().trim())
    85‚Üí          .filter(tag => tag.length > 0)
    86‚Üí      )];
    87‚Üí
    88‚Üí      // Get current evidence to preserve structured tags
    89‚Üí      const evidence = coreStore.getEvidenceById(evidenceId);
    90‚Üí      if (!evidence) {
    91‚Üí        throw new Error('Evidence document not found');
    92‚Üí      }
    93‚Üí
    94‚Üí      // Update in Firestore - preserve structured tags, update legacy tags
    95‚Üí      const evidenceRef = doc(db, 'teams', teamId, 'evidence', evidenceId);
    96‚Üí      await updateDoc(evidenceRef, {
    97‚Üí        tags: cleanedTags, // Legacy tags for backward compatibility
    98‚Üí        legacyTags: cleanedTags, // Explicit legacy tags storage
    99‚Üí        tagCount: cleanedTags.length, // Simple legacy tag count
   100‚Üí        lastTaggedAt: serverTimestamp(),
   101‚Üí        taggedBy: 'manual',
   102‚Üí        updatedAt: serverTimestamp(),
   103‚Üí      });
   104‚Üí
   105‚Üí      console.log(`[OrganizerStore] Updated legacy tags for evidence ${evidenceId}:`, cleanedTags);
   106‚Üí      return true;
   107‚Üí    } catch (err) {
   108‚Üí      console.error('[OrganizerStore] Failed to update tags:', err);
   109‚Üí      throw err;
   110‚Üí    }
   111‚Üí  };
   112‚Üí
   113‚Üí  /**
   114‚Üí   * Legacy method: Add a single tag (backward compatibility)
   115‚Üí   */
   116‚Üí  const addTag = async (evidenceId, tagText) => {
   117‚Üí    try {
   118‚Üí      const evidence = coreStore.getEvidenceById(evidenceId);
   119‚Üí      if (!evidence) {
   120‚Üí        throw new Error('Evidence document not found');
   121‚Üí      }
   122‚Üí
   123‚Üí      const currentTags = evidence.tags || evidence.legacyTags || [];
   124‚Üí      const newTag = tagText.toString().trim();
   125‚Üí      
   126‚Üí      if (!newTag) {
   127‚Üí        throw new Error('Tag cannot be empty');
   128‚Üí      }
   129‚Üí
   130‚Üí      if (currentTags.includes(newTag)) {
   131‚Üí        return; // Tag already exists
   132‚Üí      }
   133‚Üí
   134‚Üí      const updatedTags = [...currentTags, newTag];
   135‚Üí      await updateEvidenceTags(evidenceId, updatedTags);
   136‚Üí    } catch (err) {
   137‚Üí      console.error('[OrganizerStore] Failed to add tag:', err);
   138‚Üí      throw err;
   139‚Üí    }
   140‚Üí  };
   141‚Üí
   142‚Üí  /**
   143‚Üí   * Legacy method: Remove a single tag (backward compatibility)
   144‚Üí   */
   145‚Üí  const removeTag = async (evidenceId, tagText) => {
   146‚Üí    try {
   147‚Üí      const evidence = coreStore.getEvidenceById(evidenceId);
   148‚Üí      if (!evidence) {
   149‚Üí        throw new Error('Evidence document not found');
   150‚Üí      }
   151‚Üí
   152‚Üí      const currentTags = evidence.tags || evidence.legacyTags || [];
   153‚Üí      const updatedTags = currentTags.filter(tag => tag !== tagText);
