    33→<script setup>
    34→import { useAuthStore } from '../stores/auth'
    35→
    36→const authStore = useAuthStore()
    37→
    38→// Available apps configuration - matches the roadmap specs
    39→const availableApps = [
    40→  {
    41→    name: 'BookKeeper',
    42→    subdomain: 'bookkeeping',
    43→    description: 'Financial documents and records',
    44→    icon: '📚',
    45→    port: '5174'
    46→  },
    47→  {
    48→    name: 'Intranet',
    49→    subdomain: 'intranet',
    50→    description: 'Internal portal and resources',
    51→    icon: '📇',
    52→    port: '5175'
    53→  }
    54→]
    55→
    56→// Get the base domain from environment
    57→const baseDomain = import.meta.env.VITE_APP_DOMAIN || 'localhost:3000'
    58→
    59→/**
    60→ * Generate URL for an app subdomain
    61→ */
    62→const getAppUrl = (subdomain) => {
    63→  // Find the app configuration for the subdomain
    64→  const app = availableApps.find(a => a.subdomain === subdomain)
    65→  
    66→  // For local development - use specific ports
    67→  if (baseDomain.includes('localhost')) {
    68→    const port = app?.port || '5173'
    69→    return `http://localhost:${port}`
    70→  }
    71→  
    72→  // For production
    73→  return `https://${subdomain}.${baseDomain}`
    74→}
    75→
    76→
    77→/**
    78→ * Handle app switching with analytics/logging
    79→ */
    80→const handleAppSwitch = (app) => {
